A = {'ab' : 'ba', 'aa' : 'aa', 'bb' : 'bb', 'ba' : 'ab'}

key = 'ac'
if key in A:
    del A[key]

try:
    del A[key]
except KeyError:
	print('There is no element with key "' + key + '" in dict')
print(A)

A = dict(zip('abcdef', list(range(6))))
for key in A:
    print(key, A[key])

A = dict(zip('abcdef', list(range(6))))
for key, val in A.items():
    print(key, val)
